const Server = require("./server/server"),
  config = require("./config"),
  auth = require("./auth/auth"),
  recipeRoutes = require("./routes/recipes").routes,
  staticRoutes = require("./routes/static").routes,
  shoppingListRoutes = require("./routes/shoppingList").routes,
  usersRoutes = require("./routes/users").routes,
  app = new Server();
app.use(Server.JsonParser),
  app.use(Server.CookiesParser),
  app.use("/", recipeRoutes),
  app.use("/", shoppingListRoutes),
  app.use("/", usersRoutes),
  app.use("/", staticRoutes),
  app.listen(process.env.PORT || config.port, () => {
    console.log("Server listning on port " + (process.env.PORT || config.port));
  });
