const puppeteer = require("puppeteer"),
  URL = "https://cre-ate-recipe.herokuapp.com/source/index.html";
describe("Login page", () => {
  beforeAll(async () => {
    await page.goto(URL), await page.waitForTimeout(500);
  }),
    it("login by placeholder value", async () => {
      await page.waitForSelector("#login-button"),
        await page.click("#login-button");
    }),
    it("return to login page from home page", async () => {
      await page.goBack();
      const a = await page.evaluate(() => location.href);
      expect(a).toMatch(URL);
    }),
    it("go back to home page", async () => {
      await page.goto("https://zen-recipe-9eb67.web.app/home.html"),
        await page.waitForTimeout(300);
    });
});
