fetch("/recipes/", {
  method: "GET",
  headers: {
    "Content-Type": "application/json",
    "Access-Control-Allow-Origin": "*",
    "Access-Control-Allow-Headers": "Content-Type",
    "Access-Control-Allow-Methods": "GET,POST,OPTIONS,DELETE,PUT,PATCH",
  },
})
  .then((a) => a.json())
  .then((a) => {
    console.log("Success:", a);
    for (let b = 0; b < a.length; b++) {
      const c = document.getElementsByClassName("recipe-list")[0],
        d = document.createElement("div");
      d.setAttribute("class", "recipe"),
        d.setAttribute("id", a[b].title),
        (d.href = "recipe.html"),
        d.addEventListener("click", function () {
          localStorage.setItem("id", a[b].title),
            (window.location = "recipe.html");
        });
      const e = document.createElement("p");
      (e.innerHTML = "Time taken: " + a[b].cookingTime), d.appendChild(e);
      const f = document.createElement("h3");
      (f.innerHTML = "Recipe name: " + a[b].title),
        d.appendChild(f),
        c.appendChild(d);
    }
  })
  .catch((a) => {
    console.error("Error:", a);
  });
